import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import searchContext from "./context";
import * as serviceWorker from './serviceWorker';

const root = document.getElementById('root');

//function fetchGithubUsers() {
//return fetch(`https://api.github.com/search/users?q=${query}`)
// .then(response => response.json())
//}

function Header() {
  return (
    <div>
      <h1>Github user searcher</h1>
    </div>
  )
}

class Searchbar extends React.Component {
  state = {
    searchTerm: '',
    isLoading: true,
    usersArray: []
  }

  fetchGithubUsers(user) {
    console.log(user);

    fetch(`https://api.github.com/search/users?q=${user}`)
      .then(response => response.json())
      .then(data => {
        const array = data.items.map(item => console.log(item.login))
      })
  }

  handleSearchChange = (event) => {
    const searchedValue = event.target.value
    this.setState({
      searchTerm: searchedValue
    })
    this.fetchGithubUsers(this.state.searchTerm)
  }


  render() {
    return (
      <div>
        <input type='text' placeholder='Search for user' value={this.state.searchTerm} onChange={this.handleSearchChange} />
      </div>
    )
  }
}



function App() {
  return (
    <div className='container'>
      <Header />
      <Searchbar />
    </div>
  )
}




ReactDOM.render(<App />, root);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
