import React from 'react';
import searchContext from "./context";
import './index.css';
import UserRepo from "./UserRepo";
import { render } from 'react-dom';
//import { render } from 'react-dom';


class UserList extends React.Component() {


    handleClick = () => {
        this.setState({
            isClicked: true
        })
    }

    render() {
        const { usersArray } = React.useContext(searchContext);
        const { isClicked } = React.useContext(searchContext);
        return (
            <div className='lists'>
            {usersArray.map(user =>
                <li key={user.id} className='list'>
                    <a href={user.login} onClick={this.handleClick}>{user.login}</a>
                    <img alt="avatar" style={{ width: '50px' }} src={user.avatar_url} />
                </li>)}
            {isClicked ? <UserRepo /> : null} 
        </div>
        )
      }
}

export default UserList;
