import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import searchContext from "./context";
import * as serviceWorker from './serviceWorker';

const root = document.getElementById('root');
const ContextProvider = searchContext.Provider;

function fetchGithubUsers(query) {
  if (query !== '') {
    const URL = `https://api.github.com/search/users?q=${query}`
    return fetch(URL,{mode:'no-cors', headers:{'Access-Control-Allow-Origin':'*'}}).then(response => response.json())
  }
}

function Header() {
  return (
    <div>
      <h1>Github user searcher</h1>
    </div>
  )
}

class UserList extends React.Component {
  render() {
    return (
      <div>
        <li className='list'>{this.props.user}</li>
      </div>
    )
  }
}

class Searchbar extends React.Component {
  state = {
    searchTerm: '',
    isLoading: false,
    usersArray: []
  }

  handleSearchChange = (event) => {
    const searchedValue = event.target.value
    this.setState({
      searchTerm: searchedValue
    })

    fetchGithubUsers(searchedValue)
      .then(data => {
        const users = data.items.map(user => user.login)
        console.log(users);
        this.setState({
          usersArray: users,
          isLoading: true
        })
      }).finally(() => this.setState({isLoading: false}))
  }

  render() {
    return (
      <div>
        <input type='text' placeholder='Search for user' value={this.state.searchTerm} onChange={this.handleSearchChange} />
        {this.state.isLoading ? <p>Loading...</p> : (this.state.usersArray.length === 0 || this.state.searchTerm === '')? <p>No results</p> :
          <div>
            <ul>
              {this.state.usersArray.map(user => (
                <UserList user={user} />
              ))}
            </ul>
          </div>
        }
      </div>
    )
  }
}

function App() {
  return (
    <div className='container'>
      <Header />
      <Searchbar />
    </div>
  )
}

ReactDOM.render(<App />, root);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
